import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
export default function Login(props){

    const history=useNavigate()
    const [email,setEmail]=useState(' ')
    const [password,setPassword]=useState(' ')

    async function submit(e){
        e.preventDefault();

        try{
            await axios.post("http://localhost:3000/signup",{
                email,password
            })
            .then(res=>{
                if(res.data==="exist"){
                    alert("User already exists")
                }
                else if(res.data==="notexit"){
                    history("/login")
                }
            })
            .catch(e=>{
                alert("wrong details");
                console.log(e);
            })
        }
        catch(e){
            console.log(e);
        }
    }
    return(
        <>
    <h1 className={`text-${props.mode==="light"?"rgb(43, 45, 106)":"white"}`}>SignUp</h1>
    <div className="container">
    <form action="POST" className="my-3">
  <div class="row mb-3">
    {/* <label for="inputEmail3" class="col-sm-2 col-form-label">Email</label> */}
    <div class="col-sm-5">
      <input type="email" onChange={(e)=>{setEmail(e.target.value)}} class="form-control"  placeholder="Email"/>
    </div>
  </div>
  <div class="row mb-3">
    {/* <label for="inputPassword3" class="col-sm-2 col-form-label">Password</label> */}
    <div class="col-sm-5">
      <input type="password" class="form-control" onChange={(e)=>{setPassword(e.target.value)}} placeholder="Password" />
    </div>
  </div>
  <div>
  <input type="submit" class="btn btn-primary mx-2 my-2" onClick={submit}/>
  <Link to="/login" class="btn btn-primary ">Click here to Login</Link>
  </div>
  </form>
    </div>
    </>
        // <div className="signup">
        //     <h1>SignUp</h1>

        //     <form action="POST">
        //         <input type="email" onChange={(e)=>{setEmail(e.target.value)}} placeholder="Email" />
        //         <input type="password" onChange={(e)=>{setPassword(e.target.value)}} placeholder="Password" />

        //         <input type="submit" onClick={submit}/>
        //     </form>
        //     <br />

        //     <p>Or</p>

        //     <Link to="/login">Login Page</Link>
        // </div>
    )
}
