import React, { useState } from 'react'
import expenseContext from './expenseContext'
const expenseState = (props) => {
    const initialIncome = []
    const [incomes, setIncomes] = useState(initialIncome)

    const addIncome = async (income) => {
        console.log("Adding an income")

        const response = await fetch(`http://localhost:5000/api/v1/add-income`, {
            method: 'POST',
            headers: {
                'content-type': 'application/json',
                'auth-token': localStorage.getItem('token')
            },
            body: JSON.stringify(income)
        })
        const json = response.json();
        console.log(json)

        const addin = {
            "user": "64719ad62d608481ee49fe86",
            "title": income.title,
            "amount": income.amount,
            "type": "income",
            "date": income.date,
            "category": income.category,
            "description": income.description,
            "_id": "647def50c456489e555862e0",
            "createdAt": "2023-06-05T14:21:04.712Z",
            "updatedAt": "2023-06-05T14:21:04.712Z",
            "__v": 0
        }
        setIncomes(income.concat(addin))
        alert("Income added")
    }
    return (
        <expenseContext.Provider value={{ incomes, addIncome }}>
            {props.children}
        </expenseContext.Provider>
    )
}

export default expenseState
