import React, { useContext, useEffect, useState } from "react";
import blogContext from "../context/blogs/BlogContext";
import Allitem from "./Allitem"
import "./Blogitem.css"


export default function Blog(props) {
    const context = useContext(blogContext)
    const { blog, getAll } = context
    const [email, setEmail] = useState('');

    useEffect(() => {
        getAll()
        fetch(`http://localhost:5000/api/auth/${blogs.user}`)
            .then(response => response.json())
            .then(data => setEmail(data.email))
            .catch(error => console.log(error));

    }, []);

    return (
        <>

            <h1 className={`headuser text-${props.mode === "light" ? "rgb(95, 158, 160)" : "white"}`}>All Blogs</h1>
            <div className="row my-3">
                {blog.map((blogs) => {
                    return <Allitem blogs={blogs} mode={props.mode} user={email} />
                })}
            </div>
        </>
    )
}
