import React, { useState } from "react";
// import axios from "axios";
import { Link} from "react-router-dom";
import "./Login.css";

export default function Login(props){

    // const history=useNavigate();

    const [email,setEmail]=useState(' ')
    const [password,setPassword]=useState(' ')

    // async function submit(e){
    //     e.preventDefault();

    //     try{
    //         await axios.post("http://localhost:3000/login",{
    //             email,password
    //         }).then(res=>{
    //             if(res.data==="exist"){
    //                 history("/main")  //to go to after login
    //             }
    //             else if(res.data==="notexit"){
    //                 alert("User have not signup")
    //             }
    //         })
    //         .catch(e=>{
    //             alert("wrong details");
    //             console.log(e);
    //         })
    //     }
    //     catch(e){
    //         console.log(e);
    //     }
    // }
    
    function handleSubmit(e) {
        e.preventDefault();
    
        console.log(email, password);
        fetch("http://localhost:3000/login", {
          method: "POST",
          crossDomain: true,
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
            "Access-Control-Allow-Origin": "*",
          },
          body: JSON.stringify({
            email,
            password,
          }),
        })
          .then((res) => res.json())
          .then((data) => {
            console.log(data, "userRegister");
            if (data.status == "ok") {
              alert("login successful");
              window.localStorage.setItem("token", data.data);
              window.localStorage.setItem("loggedIn", true);
    
              window.location.href = "./main";
            }
          });
      }
    return(
    <>
    <h1 className={`text-${props.mode==="light"?"rgb(43, 45, 106)":"white"}`}>Login</h1>
    <div className="container">
    <form action="POST" className="my-3">
  <div class={`row mb-3 text-${props.mode==='light'?'dark':'light'}`}>
    {/* <label for="inputEmail3" class="col-sm-2 col-form-label">Email</label> */}
    <div class="col-sm-5">
      <input type="email" onChange={(e)=>{setEmail(e.target.value)}} class="form-control"  placeholder="Email"/>
    </div>
  </div>
  <div class="row mb-3">
    {/* <label for="inputPassword3" class="col-sm-2 col-form-label">Password</label> */}
    <div class="col-sm-5">
      <input type="password" class="form-control" onChange={(e)=>{setPassword(e.target.value)}} placeholder="Password" />
    </div>
  </div>
  <div>
  <input type="submit" class="btn btn-primary mx-2 my-2" onClick={handleSubmit}/>
  <Link to="/signup" class="btn btn-primary bg-chocolate">Click here to SignUp</Link>
  </div>
  </form>
    </div>
    </>
        // <div className={`login text-align-center justify-content-center`}>
        //     <h1>Login</h1>

        //     <form action="POST">
        //         <input type="email" onChange={(e)=>{setEmail(e.target.value)}} placeholder="Email"/>
        //         <br />
        //         <input type="password" onChange={(e)=>{setPassword(e.target.value)}} placeholder="Password" />
        //         <br />
        //         <input type="submit" onClick={submit}/>
        //     </form>
        //     <br />

        //     <p>Or</p>
        //     <br />

        //     <Link to="/signup">SignUp Page</Link>
        // </div>
    )
}
