import React, { useState } from "react";
import "./Write.css"
import { Link, useNavigate } from "react-router-dom"

export default function Write(props) {
    const history = useNavigate()
    const [blog, setBlog] = useState({ title: '', description: '', tag: '' })

    const handleSubmit = async (e) => {
        e.preventDefault();
        const { title, description, tag } = blog;

        const response = await fetch("http://localhost:5000/api/blogs/write", {
            method: 'POST',
            headers: {
                'auth-token': localStorage.getItem('token'),
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ title: blog.title, description: blog.description, tag: blog.tag })
        });
        const json = await response.json()
        console.log(json)
        if (json.success) {
            history("/")
            alert("Blog added Successfully!!")
        }
        else {
            alert("Follow the rules")
        }
    }

    const onChange = (e) => {
        setBlog({ ...blog, [e.target.name]: e.target.value })
    }

    return (
        <>
            <nav class={`navbar navbar-expand-lg navbar-${props.mode} bg-${props.mode}`}>
                <div class="container-fluid">
                    <div class="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul class="navbar-nav">
                            <li class="nav-item">
                                <Link class="nav-link active" aria-current="page" to="/blog">Home</Link>
                            </li>
                            <li class="nav-item">
                                <Link class="nav-link active" aria-current="page" to="/write">Write</Link>
                            </li>
                            <li class="nav-item">
                                <Link class="nav-link active" aria-current="page" to="/about">About</Link>
                            </li>
                            <li class="nav-item">
                                <Link class="nav-link active" aria-current="page" to="/">Logout</Link>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
            <h1 className="set my-3"><img src="./download.png" alt="Loading" width={`100vh`} height={`100vh`} /></h1>
            <h1 className={`text-${props.mode === "light" ? "rgb(43, 45, 106)" : "white"}`}>Write a Blog Here</h1>
            <div className="container">
                <form action="POST" className="my-3" onSubmit={handleSubmit}>

                    {/* <div className="row1 mb-3">
                        {file && (
                            <img src={URL.createObjectURL(file)} alt="Uploaded Image" width={`60%`} height={`60%`} />
                        )}
                        <div className="row1 mt-3">
                            <input type="file" id="imageInput" name="imageInput" onChange={handleFileChange} />
                        </div>
                    </div> */}
                    <div class={`row mb-3 text-${props.mode === 'light' ? 'dark' : 'light'}`}>
                        {/* <label for="inputEmail3" class="col-sm-2 col-form-label">Email</label> */}
                        <div class="col-sm-5">
                            <input type="text" class="form-control" id="title" name="title" onChange={onChange} placeholder="Title" />
                        </div>
                    </div>

                    <div class="row mb-3">
                        {/* <label for="inputPassword3" class="col-sm-2 col-form-label">Password</label> */}
                        <div class="col-sm-5">
                            <textarea type="text " class="form-control" id="description" name="description" onChange={onChange} placeholder="Description" />
                        </div>
                    </div>
                    <div class="row mb-3">
                        {/* <label for="inputPassword3" class="col-sm-2 col-form-label">Password</label> */}
                        <div class="col-sm-5">
                            <textarea type="text " class="form-control" id="tag" name="tag" onChange={onChange} placeholder="Tag" />
                        </div>
                    </div>
                    <div>
                        <input type="submit" class="btn btn-primary mx-2 my-2" />
                        {/* <Link to="/signup" class="btn btn-primary bg-chocolate">Click here to SignUp</Link> */}
                    </div>
                </form>
                <footer>
                    <div class={`container2 container2-${props.mode} bg-${props.mode === "light" ? "rgb(29, 79, 96)" : "dark"} `}>
                    </div>
                </footer>
            </div>

        </>
        // <h1>Hello</h1>
    )
}
