import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { Link } from "react-router-dom";
import './Login.css'
// import tt from './tt.png';


const Login = (props) => {
  const [credentials, setCredentials] = useState({ email: "", password: "" })
  let history = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch("http://localhost:5000/api/auth/login", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ email: credentials.email, password: credentials.password })
    });
    const json = await response.json()
    console.log(json);
    if (json.success) {
      // Save the auth token and redirect
      localStorage.setItem('token', json.authtoken);
      history.push("/blog");

    }
    else {
      alert("Invalid credentials");
    }
  }

  const onChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value })
  }

  return (
    <>
      <h1 className="set"><img src="./download.png" alt="Loading" width={`100vh`} height={`100vh`} /></h1>
      <h1 className={`text-${props.mode === "light" ? "rgb(43, 45, 106)" : "white"}`}>Login</h1>
      <div className="container">
        <form className="login-form" onSubmit={handleSubmit}>

          <div className="textbox">

            {/* <input type="email" placeholder="email" value={credentials.email} onChange={onChange} /> */}
            <input type="email" className="login-form" value={credentials.email} onChange={onChange} id="email" placeholder="Email" name="email" aria-describedby="emailHelp" />

          </div>

          <div className="textbox">

            {/* <input type="password" placeholder="Password" value={credentials.password} onChange={onChange} /> */}
            <input type="password" className="login-form" value={credentials.password} onChange={onChange} placeholder="Password" name="password" id="password" />

          </div>

          <button type="submit">LOGIN</button>
          <a href="/">Back..</a>
        </form>
      </div>
    </>


  )
}

export default Login
