import React, { useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import ExpContext from '../context/exp/ExpContext';
import Form from './Form';
import IncomeItem from './Incomeitem';

const Income = () => {
    const context = useContext(ExpContext);
    const { addIncome, incomes, getIncomes, deleteIncome, totalIncome } = context;
    const history = useNavigate();

    useEffect(() => {
        // Assuming the conditional routing is based on authentication
        const token = localStorage.getItem('token');
        if (token) {
            // User is authenticated, continue to income page
            // Add your logic or API calls here if needed
        } else {
            // User is not authenticated, redirect to home page
            history('/');
        }
    }, []); // Empty dependency array to run the effect only once when the component mounts

    return (
        <IncomeStyled>
            <div className="income-content">
                <div className="form-container">
                    <h2 className="total-income">Total Income: {totalIncome()}</h2>
                    <h1>Incomes</h1>
                    <Form addIncome={addIncome} />
                </div>
                <div className="incomes">
                    {/* Render income items */}
                    {incomes.map((income) => (
                        <IncomeItem key={income.id} income={income} deleteIncome={deleteIncome} />
                    ))}
                </div>
            </div>
        </IncomeStyled>
    );
};

const IncomeStyled = styled.div`
  // Add your styling here
`;

export default Income;
