import React, { useState, useEffect } from "react";
// import blogContext from "../context/blogs/BlogContext";
import Blogitem from "../components/Blogitem"
import { useNavigate } from "react-router-dom";
import "./Blogitem.css"

export default function UserData(props) {

    // const context = useContext(blogContext)
    // const history = useNavigate()
    // const { blog, getBlogs, users } = context
    // useEffect(() => {
    //     if (localStorage.getItem('token')) {
    //         getBlogs()
    //     }
    //     else {
    //         history("/")
    //     }

    // }, [])
    const [blog, setBlog] = useState(null);
    const [user, setUser] = useState(null);

    useEffect(() => {
        // Fetch blog details from MongoDB API endpoint
        const fetchBlogDetails = async () => {
            try {
                const response = await fetch(`/api/blogs/${blogId}`); // replace with your API endpoint for fetching blog details
                const data = await response.json();
                setBlog(data.blog);
                setUser(data.user);
            } catch (error) {
                console.error('Failed to fetch blog details:', error);
            }
        };

        fetchBlogDetails();
    }, []);
    return (
        <>
            <h1 className={`headuser text-${props.mode === "light" ? "rgb(95, 158, 160)" : "white"}`}>Your Blogs</h1>
            <div className="row my-3">
                {blog.map((blogs) => {
                    return <Blogitem key={blogs._id} blogs={blogs} mode={props.mode} user={user.name} />
                })}

            </div>
        </>
    )
}
