import React, { useEffect, useContext } from 'react'
import { useNavigate } from 'react-router-dom'
import ExpContext from '../context/exp/ExpContext';
import ExpForm from './ExpForm';
function Expense() {
    const context = useContext(ExpContext)
    const { addExpense, expenses, getExpense, deleteExpense, totalExpense, exptotal } = context;
    const history = useNavigate();
    useEffect(() => {
        if (localStorage.getItem('token')) {
            getExpense();
            history("/expense")
        }
        else {
            history("/")
        }
    }, [])
    return (
        <ExpenseStyled>
            <div class="container-fluid">
                <h1 className='my-2'>Expense Section</h1>
                <div class="row">
                    <div class="setcol col-md-4">
                        <h2 className="total-income">Total Expense: {exptotal} </h2>
                        <ExpForm />
                    </div>
                    <div class="col">
                        <h2 className="total-income">Analize Your Incomes</h2>
                        {expenses.length > 0 ? (expenses.map((expense) => {
                            const { _id, title, amount, date, category, description, type } = expense;
                            return <IncomeItem
                                key={_id}
                                id={_id}
                                title={title}
                                description={description}
                                amount={amount}
                                date={date}
                                type={type}
                                category={category}
                                indicatorColor="var(--color-green)"
                                deleteItem={deleteExpense}
                            />
                        })) : (
                            <h1>Loading Please Wait :) </h1>
                        )
                        }
                    </div>
                </div>


            </div>

        </ExpenseStyled>
    )
}
const ExpenseStyled = styled.div`
    display: flex;
    overflow: auto;
    .total-income{
        display: flex;
        justify-content: center;
        align-items: left;
        background: #FCF6F9;
        border: 2px solid #FFFFFF;
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        border-radius: 20px;
        padding: 1rem;
        margin: 1rem 0;
        font-size: 2rem;
        gap: .5rem;
        span{
            font-size: 2.5rem;
            font-weight: 800;
            color: var(--color-green);
        }
    }
    h1{
        color:#68f118;
        text-align: center;
        font-style:italic;
    }

    .income-content{
        display: flex;
        gap: 2rem;
        .incomes{
            flex: 1;
        }
    }
`;
export default Expense
