import React, { useState } from "react";
import "./Write.css"
import axios from "axios";
import { useNavigate } from "react-router-dom";
export default function Write(props) {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [file, setFile] = useState(null);

    const handleTitleChange = (event) => {
        setTitle(event.target.value);
    };

    const handleDescriptionChange = (event) => {
        setDescription(event.target.value);
    };

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        // formData.append('file', file);
        try {
            await axios.post('http://localhost:3000/write', {
                title, description
            }).then(res => {
                if (res.data === "exist") {
                    history("/")
                    alert("User already exists")
                }
                else if (res.data === "notexist") {
                    history("/signup")
                }
            })
                .catch(e => {
                    alert("wrong details");
                    console.log(e);
                })
            // alert('File uploaded successfully!');
        } catch (error) {
            console.error(error);
            alert('Error uploading file');
        }
    };

    return (
        <>
            <nav class={`navbar navbar-expand-lg navbar-${props.mode} bg-${props.mode}`}>
                <div class="container-fluid">
                    <div class="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul class="navbar-nav">
                            <li class="nav-item">
                                <a class="nav-link active" aria-current="page" href="/blog">Home</a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link active" aria-current="page" href="/write">Write</a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link active" aria-current="page" href="/about">About</a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link active" aria-current="page" href="/">Logout</a>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
            <h1 className="set my-3"><img src="./download.png" alt="Loading" width={`100vh`} height={`100vh`} /></h1>
            <h1 className={`text-${props.mode === "light" ? "rgb(43, 45, 106)" : "white"}`}>Write a Blog Here</h1>
            <div className="container">
                <form action="POST" className="my-3">

                    <div className="row1 mb-3">
                        {file && (
                            <img src={URL.createObjectURL(file)} alt="Uploaded Image" width={`60%`} height={`60%`} />
                        )}
                        <div className="row1 mt-3">
                            <input type="file" id="imageInput" name="imageInput" onChange={handleFileChange} />
                        </div>
                    </div>
                    <div class={`row mb-3 text-${props.mode === 'light' ? 'dark' : 'light'}`}>
                        {/* <label for="inputEmail3" class="col-sm-2 col-form-label">Email</label> */}
                        <div class="col-sm-5">
                            <input type="text" class="form-control" value={title} onChange={handleTitleChange} placeholder="Title" />
                        </div>
                    </div>

                    <div class="row mb-3">
                        {/* <label for="inputPassword3" class="col-sm-2 col-form-label">Password</label> */}
                        <div class="col-sm-5">
                            <textarea value={description} class="form-control" onChange={handleDescriptionChange} placeholder="Description" />
                        </div>
                    </div>
                    <div>
                        <input type="submit" class="btn btn-primary mx-2 my-2" onSubmit={handleSubmit} />
                        {/* <Link to="/signup" class="btn btn-primary bg-chocolate">Click here to SignUp</Link> */}
                    </div>
                </form>
                <footer>
                    <div class={`container1 container1-${props.mode} bg-${props.mode === "light" ? "rgb(29, 79, 96)" : "dark"} `}>
                        <div class="copyright">
                        </div>
                        <div class="credits">
                        </div>
                    </div>
                </footer>
            </div>

        </>
    )
}
