import React, { useState } from "react";
import "./Write.css"
import { Link } from "react-router-dom"
import axios from "axios";
export default function Write(props) {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [file, setFile] = useState(null);

    const handleTitleChange = (e) => {
        setTitle(e.target.value);
    };

    const handleDescriptionChange = (e) => {
        setDescription(e.target.value);
    };

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append('file', file);
            formData.append('title', title);
            formData.append('description', description);
            await axios.post('write', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            alert('File uploaded successfully!');
            // Reset form fields
            setTitle('');
            setDescription('');
            setFile(null);
        } catch (error) {
            console.error(error);
            alert('Failed to upload file.');
        }
    };

    return (
        <>
            <nav class={`navbar navbar-expand-lg navbar-${props.mode} bg-${props.mode}`}>
                <div class="container-fluid">
                    <div class="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul class="navbar-nav">
                            <li class="nav-item">
                                <Link class="nav-link active" aria-current="page" to="/blog">Home</Link>
                            </li>
                            <li class="nav-item">
                                <Link class="nav-link active" aria-current="page" to="/write">Write</Link>
                            </li>
                            <li class="nav-item">
                                <Link class="nav-link active" aria-current="page" to="/about">About</Link>
                            </li>
                            <li class="nav-item">
                                <Link class="nav-link active" aria-current="page" to="/">Logout</Link>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
            <h1 className="set my-3"><img src="./download.png" alt="" width={`100vh`} height={`100vh`} /></h1>
            <h1 className={`text-${props.mode === "light" ? "rgb(43, 45, 106)" : "white"}`}>Write a Blog Here</h1>
            <div className="container">
                <form action="POST" className="my-3" onSubmit={handleSubmit}>

                    <div className="row1 mb-3">
                        {file && (
                            <image src={URL.createObjectURL(file)} alt="Uploaded Image" width={`60%`} height={`60%`} />
                        )}
                        <div className="row1 mt-3">
                            <input type="file" id="file" name="imageInput" onChange={handleFileChange} />
                        </div>
                    </div>
                    <div class={`row mb-3 text-${props.mode === 'light' ? 'dark' : 'light'}`}>
                        {/* <label for="inputEmail3" class="col-sm-2 col-form-label">Email</label> */}
                        <div class="col-sm-5">
                            <input type="text" id="title"
                                value={title} class="form-control" onChange={handleTitleChange} placeholder="Title" />
                        </div>
                    </div>

                    <div class="row mb-3">
                        {/* <label for="inputPassword3" class="col-sm-2 col-form-label">Password</label> */}
                        <div class="col-sm-5">
                            <textarea type="text " id="description"
                                value={description} class="form-control" onChange={handleDescriptionChange} placeholder="Description" />
                        </div>
                    </div>
                    <div>
                        <button type="submit" class="btn btn-primary mx-2 my-2" >Upload</button>
                        {/* <Link to="/signup" class="btn btn-primary bg-chocolate">Click here to SignUp</Link> */}
                    </div>
                </form>
                <footer>
                    <div class={`container2 container2-${props.mode} bg-${props.mode === "light" ? "rgb(29, 79, 96)" : "dark"} `}>
                    </div>
                </footer>
            </div>

        </>
        // <h1>Hello</h1>
    )
}
