import './App.css';
import About from './components/About';
import Navbar from './components/navbar';
import React,{useState} from "react";
import{
  BrowserRouter as Router,
  Routes,
  Route
} from "react-router-dom";

function App() {
  const [mode,setMode]=useState('light')

  const toggleMode=()=>{
    if(mode==='light'){
      setMode("dark")
      document.body.style.backgroundColor="#495057"
      document.title="Blogging-DarkMode"
    }
    else{
      setMode("light")
      document.body.style.backgroundColor="white"

      document.title="Blogging-LightMode"
    }
  }
  return (
    <>
    <Router>
      <Navbar title="Wiseverse" toggleMode={toggleMode} mode={mode}></Navbar>
        <div className="container">
        <Routes>
          <Route path="/about" element={<About title="About" mode={mode}/>}/>
        </Routes>
        </div>
        </Router>
    </>
  );
}

export default App;
