import './App.css';
import About from './components/About';
import Blog from './components/Blog';
import Navbar from './components/navbar';
import React,{useState} from "react";
import{
  BrowserRouter as Router,
  Routes,
  Route
} from "react-router-dom";
import Login from './components/Login';
import Signup from './components/Signup';

function App() {
  const [mode,setMode]=useState('light') //hooks

  const toggleMode=()=>{   //function of toggling
    if(mode==='light'){
      setMode("dark")
      document.body.style.backgroundColor="#495057"
      document.title="Blogging-DarkMode"
    }
    else{
      setMode("light")
      document.body.style.backgroundColor="white"
      document.title="Blogging-LightMode"
    }
  }
  return (
    <>
    <Router>
      <Navbar title="Wiseverse_Blog" toggleMode={toggleMode} mode={mode}></Navbar>
        <div className="container my-4">
        <Routes>
          <Route path="/" element={<Blog/>}/>
          <Route path="/about" element={<About title="About" mode={mode}/>}/>
          <Route path="/login" element={<Login/>}/>
          <Route path="/signup" element={<Signup/>}/>
        </Routes>
        </div>
        </Router>
    </>
  );
}

export default App;
