import React, { useState } from 'react'
import ExpContext from './ExpContext'

const ExpState = (props) => {
    const [incomes, setIncomes] = useState([]);

    const addIncome = async (income) => {
        console.log("Adding an income")

        const response = await fetch(`http://localhost:5000/api/v1/add-income`, {
            method: 'POST',
            headers: {
                'content-type': 'application/json',
                'auth-token': localStorage.getItem('token')
            },
            body: JSON.stringify(income)
        })
        const json = response.json();
        console.log(json)

        const addin = {
            "user": "64719ad62d608481ee49fe86",
            "title": income.title,
            "amount": income.amount,
            "type": "income",
            "date": income.date,
            "category": income.category,
            "description": income.description,
            "_id": "647def50c456489e555862e0",
            "createdAt": "2023-06-05T14:21:04.712Z",
            "updatedAt": "2023-06-05T14:21:04.712Z",
            "__v": 0
        }
        setIncomes(addin)
        getIncome()
        alert("Income added Successfully")
    }

    const getIncome = async () => {
        const response = await fetch(`http://localhost:5000/api/v1/get-incomes`, {
            method: 'GET',
            headers: {
                'content-type': 'application/json',
                'auth-token': localStorage.getItem('token')
            },
        })
        const json = await response.json()
        console.log(json)
        setIncomes(json)
    }

    const deleteIncome = async (id) => {
        const response = await fetch(`http://localhost:5000/api/v1/delete-income/${id}`, {
            method: 'DELETE',
            headers: {
                'content-type': 'application/json',
                'auth-token': localStorage.getItem('token')
            }
        })
        const json = response.json();
        console.log(json)

        console.log(id)
        const newincome = incomes.filter((income) => { return income._id !== id })
        setIncomes(newincome)
        getIncome()
        alert("Income Deleted Successfully")
    }

    const totalIncome = async () => {
        try {
            // const response = await fetch(`http://localhost:5000/api/v1/get-income`, {
            //     method: 'GET',
            //     headers: {
            //         'content-type': 'application/json'
            //         // 'auth-token': localStorage.getItem('token')
            //     }
            // });
            res = getIncome()

            console.log(res)
            const userIncomeData = response.amount;

            if (Array.isArray(userIncomeData)) {
                // Calculate the total income
                const totalIncome = userIncomeData.reduce(
                    (total, income) => total + income.amount,
                    0
                );

                return totalIncome;
            } else {
                console.log('User income data is not an array:', userIncomeData);
            }

        } catch (error) {
            console.log('Error fetching user income:', error);
        }
    };
    return (
        <ExpContext.Provider value={{ incomes, totalIncome, deleteIncome, addIncome, getIncome }}>
            {props.children}
        </ExpContext.Provider>
    )
}

export default ExpState
