import React, { useState, useContext } from "react";
import blogContext from "../context/blogs/BlogContext";
import "./Write.css"


export default function Write(props) {
    const context = useContext(blogContext)
    const { addBlog } = context
    const [blog, setBlog] = useState({ title: '', description: '', tag: '' })
    const [image, setImage] = useState(null);

    const handleClick = (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('title', title);
        formData.append('description', description);
        formData.append('tag', tag);
        formData.append('image', image);
        addBlog(blog.title, blog.description, blog.tag)
        setBlog({ title: '', description: '', tag: '' })
    }

    const onChange = (e) => {
        setBlog({ ...blog, [e.target.name]: e.target.value })
    }
    function handleImageChange(event) {
        setImage(event.target.files[0]);
    }

    return (
        <>
            <h1 className="set my-3"><img src="./download.png" alt="Loading" width={`100vh`} height={`100vh`} /></h1>
            <h1 className={`text-${props.mode === "light" ? "rgb(43, 45, 106)" : "white"}`}>Write a Blog Here</h1>
            <div className="container">
                <form action="POST" className="my-3" >

                    <div class={`row mb-3 text-${props.mode === 'light' ? 'dark' : 'light'}`}>
                        {/* <label for="inputEmail3" class="col-sm-2 col-form-label">Email</label> */}
                        <div class="col-sm-5">
                            <input type="text" class="form-control" id="title" name="title" onChange={onChange} value={blog.title} placeholder="Title" />
                        </div>
                    </div>

                    <div class="row mb-3">
                        {/* <label for="inputPassword3" class="col-sm-2 col-form-label">Password</label> */}
                        <div class="col-sm-5">
                            <textarea type="text " class="form-control" id="description" name="description" onChange={onChange} value={blog.description} placeholder="Description" />
                        </div>
                    </div>
                    <div class="row mb-3">
                        {/* <label for="inputPassword3" class="col-sm-2 col-form-label">Password</label> */}
                        <div class="col-sm-5">
                            <input type="text " class="form-control" id="tag" name="tag" onChange={onChange} value={blog.tag} placeholder="Tag" />
                        </div>
                    </div>
                    <div class="row mb-3">
                        {/* <label for="inputPassword3" class="col-sm-2 col-form-label">Password</label> */}
                        <div class="col-sm-5">
                            <input type="file" accept="image/*" onChange={handleImageChange} />
                        </div>
                    </div>
                    <div>
                        <input type="submit" class="btn btn-primary mx-2 my-2" onClick={handleClick} />
                        {/* <Link to="/signup" class="btn btn-primary bg-chocolate">Click here to SignUp</Link> */}
                    </div>
                </form>
                <footer>
                    <div class={`container2 container2-${props.mode} bg-${props.mode === "light" ? "rgb(29, 79, 96)" : "dark"} `}>
                    </div>
                </footer>
            </div>

        </>
        // <h1>Hello</h1>
    )
}
