import React, { useState } from "react";
import blogContext from "./BlogContext";

const BlogState = (props) => {
    // const state = {
    //     "name": "vishal",
    //     "sem": "6c"
    // }

    const BlogInitial = []
    const [blog, setBlog] = useState(BlogInitial)

    const getBlogs = async () => {
        const response = await fetch(`http://localhost:5000/api/blogs/fuserblog`, {
            method: 'GET',
            headers: {
                'content-type': 'application/json',
                'auth-token': localStorage.getItem('token')
            },
        })
        const json = await response.json()
        console.log(json)
        setBlog(json)
    }

    const getAll = async () => {
        const response = await fetch(`http://localhost:5000/api/blogs/fetchall`, {
            method: 'GET',
            headers: {
                'content-type': 'application/json'
            },
        })
        const json = await response.json()
        console.log(json)
        setBlog(json)
    }

    const getUser = async () => {
        const response = await fetch(`http://localhost:5000/api/auth/getuser`,)
    }


    const addBlog = async (title, description, tag) => {
        console.log("adding a new data")

        const response = await fetch(`http://localhost:5000/api/blogs/write`, {
            method: 'POST',
            headers: {
                'content-type': 'application/json',
                'auth-token': localStorage.getItem('token')
            },
            body: JSON.stringify({ title, description, tag })
        })
        const json = response.json();
        console.log(json)


        const blogAdd = {
            "_id": "6437ed2c9a1a01ee5ea3aaa0",
            "user": "643411e930747720356dafbb",
            "title": title,
            "description": description,
            "tag": tag,
            "timestamp": "2023-04-13T11:53:16.395Z",
            "__v": 0
        }
        setBlog(blog.concat(blogAdd))
        alert("blog has been Added Succesfully!!")
    }

    const deleteBlog = async (id) => {
        //Api call
        const response = await fetch(`http://localhost:5000/api/blogs/delete/${id}`, {
            method: 'DELETE',
            headers: {
                'content-type': 'application/json',
                'auth-token': localStorage.getItem('token')
            }
        })
        const json = response.json();
        console.log(json)

        console.log("Deleted" + id)
        const newBlogs = blog.filter((blogs) => { return blogs._id !== id })
        setBlog(newBlogs)
        alert("Blog Deleted Succesfully")
    }

    return (
        <blogContext.Provider value={{ blog, setBlog, addBlog, deleteBlog, getBlogs, getAll }}>
            {props.children}
        </blogContext.Provider>
    )
}

export default BlogState;
