import React, { useState } from "react";
import "./Write.css"
export default function Write(props){
    const [title,setTitle]=useState(' ')
    const [description,setDescription]=useState(' ')
    const [selectedFile, setSelectedFile] = useState(null);

    // async function submit(e){
    //     e.preventDefault();

    //     try{
    //         await axios.post("http://localhost:3000/write",{
    //             title,description
    //         })
    //         .then(res=>{
    //             if(res.data==="exist"){
    //                 history("/")
    //                 alert("User already exists")
    //             }
    //             else if(res.data==="notexist"){
    //                 history("/signup")
    //             }
    //         })
    //         .catch(e=>{
    //             alert("wrong details");
    //             console.log(e);
    //         })
    //     }
    //     catch(e){
    //         console.log(e);
    //     }
    // }
    return(
        <>
        <nav class={`navbar navbar-expand-lg navbar-${props.mode} bg-${props.mode}`}>
            <div class="container-fluid">
                <div class="collapse navbar-collapse" id="navbarSupportedContent">
                <ul class="navbar-nav">
                    <li class="nav-item">
                        <a class="nav-link active" aria-current="page" href="/blog">Home</a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link active" aria-current="page" href="/write">Write</a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link active" aria-current="page" href="/about">About</a>
                    </li>
                    <li class="nav-item">
                    <a class="nav-link active" aria-current="page" href="/">Logout</a>
                    </li>
                </ul>
                </div>
            </div>
        </nav>
        <h1 className="set my-3"><img src="./download.png" alt="Loading" width={`100vh`} height={`100vh`}/></h1>
    <h1 className={`text-${props.mode==="light"?"rgb(43, 45, 106)":"white"}`}>Write a Blog on Technologies</h1>
    <div className="container">
    <form action="POST" className="my-3">
        
        <div className="row mb-3">
            <input type="file" id="fileInput" name="fileInput"/>
            
        </div>
  <div class={`row mb-3 text-${props.mode==='light'?'dark':'light'}`}>
    {/* <label for="inputEmail3" class="col-sm-2 col-form-label">Email</label> */}
    <div class="col-sm-5">
    
      <input type="name" onChange={(e)=>{setTitle(e.target.value)}} class="form-control"  placeholder="Title"/>
    </div>
  </div>
  
  <div class="row mb-3">
    {/* <label for="inputPassword3" class="col-sm-2 col-form-label">Password</label> */}
    <div class="col-sm-5">
      <input type="password" class="form-control" onChange={(e)=>{setDescription(e.target.value)}} placeholder="Description" />
    </div>
  </div>
  <div>
  {/* <input type="submit" class="btn btn-primary mx-2 my-2" onClick={submit} />
  <Link to="/signup" class="btn btn-primary bg-chocolate">Click here to SignUp</Link> */}
  </div>
  </form>
    </div>
        </>
    )
}
