import React, { useState } from "react";
import blogContext from "./BlogContext";

const blogState = (props) => {
    // const state = {
    //     "name": "vishal",
    //     "sem": "6c"
    // }

    const blogsInitial = [
        {
            "_id": "6436a5410d2b3a995d36c531",
            "user": "643411e930747720356dafbb",
            "title": "my title updated1",
            "description": "Have a nice day updated1",
            "tag": "general day",
            "timestamp": "2023-04-12T12:34:09.137Z",
            "__v": 0
        },
        {
            "_id": "6436b019258995fd572deb9f",
            "user": "643411e930747720356dafbb",
            "title": "The Internet of Things (IoT): Transforming Our World",
            "description": "The Internet of Things (IoT) is a revolutionary technology that is transforming various aspects of our world, from homes and industries to cities and healthcare. The IoT refers to the interconnected network of physical devices, such as sensors, actuators, and other smart devices, that communicate and exchange data with each other through the internet, without human intervention. This has opened up new possibilities for automation, efficiency, and convenience in our daily lives, as well as in businesses and industries.",
            "tag": "#Iot",
            "timestamp": "2023-04-12T13:20:25.783Z",
            "__v": 0
        }
    ]

    const [blogs, setBlogs] = useState(blogsInitial)
    return (
        <blogContext.Provider value={{ blogs, setBlogs }}>
            {props.children}
        </blogContext.Provider>
    )
}

export default blogState;
