import React, { createContext, useContext, useState } from "react";
import axios from 'axios'
const BASE_URL = "http://localhost:5000/api/v1/";

const GlobalContext = React.createContext()
export const GLobalProvider = ({ props }) => {
    const [income, setIncome] = useState([]);
    // const [expense, setExpense] = useState([]);
    // const [error, setError] = useState(null);

    const addIncome = async (title, amount, date, category, description) => {
        console.log("Adding new Income")
        const response = await fetch(`${BASE_URL}add-income`, {
            method: "POST",
            headers: {
                'content-type': 'application/json',
                'auth-token': localStorage.getItem('token')
            },
            body: JSON.stringify({ title, amount, date, category, description })
        })
        const json = response.json();
        console.log(json)

        const Addin = {
            "user": "64719ad62d608481ee49fe86",
            "_id": "647ddd1c8db91edd77f434c3",
            "title": title,
            "amount": amount,
            "date": date,
            "category": category,
            "description": description,
            "createdAt": "2023-06-05T13:03:24.183Z",
            "updatedAt": "2023-06-05T13:03:24.183Z",
            "__v": 0

        }
        setIncome(income.concat(Addin))
        alert("Income added successfully!")

    }

    return (
        <GlobalContext.Provider value={{ income, addIncome }}>
            {props.children}
        </GlobalContext.Provider>
    )
}


export const useGlobalContext = () => {
    return useContext(GlobalContext)
}
