import React, { useContext, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import styled from 'styled-components'
import ExpContext from '../context/exp/ExpContext';
import { dollar } from '../utils/Icons';
function Dashboard() {
    const context = useContext(ExpContext);
    const { incomes, expenses, getAvailable, available, getExpense, getIncome, total, exptotal } = context;
    const history = useNavigate();
    useEffect(() => {
        if (localStorage.getItem('token')) {
            getExpense();
            getIncome();
            getAvailable();
            history("/dashboard")
        }
        else {
            history("/")
        }
    }, [])
    return (
        <DashboardStyled>
            <div class="container-fluid">
                <h1 className='heading'>Dashboard</h1>
                <div class="row">

                    <div class="col">
                        <h2 className="total-income">Analize Your Expense</h2>

                    </div>
                    <div class="setcol col-md-4 mx-4">
                        <div className="history-con">
                            <h2 className="salary-title">Min <span>Salary</span>Max</h2>
                            <div className="salary-item">

                                <p>
                                    {dollar}{Math.min(...incomes.map(item => item.amount))}
                                </p>
                                <p>
                                    {dollar}{Math.max(...incomes.map(item => item.amount))}
                                </p>
                            </div>
                            <h2 className="salary-title">Min <span>Expense</span>Max</h2>
                            <div className="salary-item">

                                <p>
                                    {dollar}{Math.min(...expenses.map(item => item.amount))}
                                </p>
                                <p>
                                    {dollar}{Math.max(...expenses.map(item => item.amount))}
                                </p>
                            </div>
                        </div>
                        <h2 className="total-income">Total Expense: {dollar}{exptotal}</h2>
                        <h2 className="total-income">Total Income: {dollar}{total}</h2>
                        <h2 className="total-income">Available Balance: {dollar}{available}</h2>

                    </div>
                </div>
            </div>

        </DashboardStyled >
    )
}

const DashboardStyled = styled.div`
    display: flex;
    overflow: auto;
    .total-income{
        display: flex;
        justify-content: center;
        align-items: left;
        background: #FCF6F9;
        border: 2px solid #FFFFFF;
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        border-radius: 20px;
        padding: 1rem;
        margin: 1rem 0;
        font-size: 2rem;
        gap: .5rem;
        span{
            font-size: 2.5rem;
            font-weight: 800;
            color: var(--color-green);
        }
    }

    .income-content{
        display: flex;
        gap: 2rem;
        .incomes{
            flex: 1;
        }
    }
    .history-con{
        grid-column: 4 / -1;
        h2{
            margin: 1rem 0;
            display: flex;
            align-items: center;
            justify-content: space-between;
        }
        .salary-title{
            font-size: 1.2rem;
            span{
                font-size: 1.8rem;
            }
        }
        .salary-item{
            background: #FCF6F9;
            border: 2px solid #FFFFFF;
            box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
            padding: 1rem;
            border-radius: 20px;
            display: flex;
            justify-content: space-between;
            align-items: center;
            p{
                font-weight: 600;
                font-size: 1.6rem;
            }
        }
    }
`;
export default Dashboard
