import React, { useContext, useEffect } from "react";
import blogContext from "../context/blogs/BlogContext";
import Blogitem from "../components/Blogitem"
import { useNavigate } from "react-router-dom";
import "./Blogitem.css"

export default function UserData(props) {

    const context = useContext(blogContext)
    const history = useNavigate()
    const { blog, getBlogs, fetchUsers, users } = context
    useEffect(() => {

        if (localStorage.getItem('token')) {
            getBlogs()
        }
        else {
            history("/")
        }
    }, [])
    return (
        <>
            <h1 className={`headuser text-${props.mode === "light" ? "rgb(95, 158, 160)" : "white"}`}>Your Blogs</h1>
            <div className="row my-3">
                {blog.map((blogs) => {
                    fetchUsers()
                    {
                        users.map((user) => {
                            return <Blogitem key={blogs._id} blogs={blogs} mode={props.mode} user={blogs.user} name={user.name} />
                        })
                    }

                })}
            </div>
            {/* <div>
                <h1>User List</h1>
                <ul>
                    {users.map(user => (
                        <li key={user._id}>
                            <strong>Name:</strong> {user.name}, <strong>Email:</strong> {user.email}
                        </li>
                    ))}
                </ul>
            </div>
            <h1>Hello</h1>
            <h1>Hello</h1>
            <h1>Hello</h1>
            <h1>Hello</h1> */}
        </>
    )
}
