import React, { useContext } from "react";
import blogContext from "../context/blogs/BlogContext";
import "./Blogitem.css"
export default function Blogitem(props) {
    const context = useContext(blogContext)
    const { deleteBlog } = context
    const { blogs } = props;
    const { user } = props
    return (
        <>
            <div className={`col-md-3 `}>
                <div class="card my-3">
                    <div class={`card-body bg-${props.mode}`}>
                        <h5 class={`card-title text-${props.mode === "light" ? "rgb(0,0,0)" : "white"}`}>{blogs.title}</h5>
                        <p class={`card-text text-${props.mode === "light" ? "rgb(0,0,0)" : "white"}`}>{blogs.description}</p>
                        <p className={`card-text text-${props.mode === "light" ? "rgb(0,0,0)" : "white"}`}>{blogs.tag}</p>
                        <p className={`card-text text-${props.mode === "light" ? "rgb(0,0,0)" : "white"}`}>{user.title}</p>
                        <i class={`fa-sharp fa-regular fa-trash-can`} onClick={() => { deleteBlog(blogs._id) }}></i>
                    </div>
                </div>
            </div>
        </>
    )
}
