import './App.css';
import About from './components/About';
import Blog from './components/Blog';
import Navbar from './components/navbar';
import Main from './components/main';
import Footer from './components/footer';
import React,{useState} from "react";
import{
  BrowserRouter as Router,
  Routes,
  Route
} from "react-router-dom";
import Login from './components/Login';
import Signup from './components/Signup';
// import DataProvider from './context/DataProvider';

// const PrivateRoute = ({ isAuthenticated, ...props }) => {
//   const token = sessionStorage.getItem('accessToken');
//   return isAuthenticated && token ? 
//     <>
//       <Header />
//       <Outlet />
//     </> : <Navigate replace to='/account' />
// };

function App() {
  const [mode,setMode]=useState('light') //hooks
  const [isAuthenticated, isUserAuthenticated] = useState(false);

  const toggleMode=()=>{   //function of toggling
    if(mode==='light'){
      setMode("dark")
      document.body.style.backgroundColor="#343a40"
      document.title="Blogging-DarkMode"
    }
    else{
      setMode("light")
      document.body.style.backgroundColor="white"
      document.title="Blogging-LightMode"
    }
  }
  return (
    <>
    <Router>
      <Navbar title="Wiseverse_Blog" toggleMode={toggleMode} mode={mode}></Navbar>
        <div className="container my-3">
        <Routes>
          <Route path="/" element={<Blog mode={mode}/>}/>
          <Route path="/about" element={<About title="Metaverse" mode={mode}/>}/>
          <Route path="/login" element={<Login mode={mode}/>}/>
          <Route path="/signup" element={<Signup mode={mode}/>}/>
          <Route path="/main" element={<Main/>}/>
        </Routes>
        </div>
        <Footer mode={mode}></Footer>
      </Router>
    </>
    // <>
    // <DataProvider>
    //   <Router>
    //     <div className="container mt-64">
    //       <Routes>
    //         <Route></Route>
    //       </Routes>
    //     </div>
    //   </Router>
    // </DataProvider>
    // </>
  );
}

export default App;
