import React, { useContext, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import styled from 'styled-components'
import ExpContext from '../context/exp/ExpContext';
import IncomeItem from './Incomeitem';
import Expenseitem from './Expenseitem';
function Transactions() {
    const context = useContext(ExpContext)
    const { Available, expenses, getExpense, deleteExpense, getIncome, incomes, deleteIncome } = context
    const history = useNavigate();
    useEffect(() => {
        if (localStorage.getItem('token')) {
            getIncome();
            getExpense();
            history("/transactions")
        }
        else {
            history("/")
        }
    }, [])
    return (
        <TransactionStyled>
            <div class="container-fluid">
                <h2 className="total-income">Available Balance: $</h2>
                <div class="row">
                    <div class="col">
                        <h2 className="total-income">Income History</h2>
                        {incomes.length > 0 ? (incomes.map((income) => {
                            const { _id, title, amount, date, category, description, type } = income;
                            return <IncomeItem
                                key={_id}
                                id={_id}
                                title={title}
                                description={description}
                                amount={amount}
                                date={date}
                                type={type}
                                category={category}
                                indicatorColor="var(--color-green)"
                                deleteItem={deleteIncome}
                            />
                        })) : (
                            <h1>Loading Please Wait :) </h1>
                        )
                        }
                    </div>
                    <div class="col">
                        <h2 className="total-income">Expense History</h2>
                        {expenses.length > 0 ? (expenses.map((expense) => {
                            const { _id, title, amount, date, category, description, type } = expense;
                            return <Expenseitem
                                key={_id}
                                id={_id}
                                title={title}
                                description={description}
                                amount={amount}
                                date={date}
                                type={type}
                                category={category}
                                indicatorColor="var(--color-green)"
                                deleteItem={deleteExpense}
                            />
                        })) : (
                            <h1>Loading Please Wait :) </h1>
                        )
                        }
                    </div>

                </div>


            </div>
        </TransactionStyled>
    )
}
const TransactionStyled = styled.div`
    display: flex;
    overflow: auto;
    .total-income{
        display: flex;
        justify-content: center;
        align-items: left;
        background: #FCF6F9;
        border: 2px solid #FFFFFF;
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        border-radius: 20px;
        padding: 1rem;
        margin: 1rem 0;
        font-size: 2rem;
        gap: .5rem;
        span{
            font-size: 2.5rem;
            font-weight: 800;
            color: var(--color-green);
        }
    }
    h1{
        color:rgb(32, 164, 164);
        text-align: left;
        font-style:italic;
    }

    .income-content{
        display: flex;
        gap: 2rem;
        .incomes{
            flex: 1;
        }
    }
`;
export default Transactions
