import './App.css';
import Navbar from './components/navbar';
import Footer from './components/footer';
import React, { useState } from "react";
import AuthContext from "./components/AuthContext";
import {
  BrowserRouter as Router,
  Routes,
  Route
} from "react-router-dom";
import Login from './components/Login';
import Signup from './components/Signup';
import Dashboard from './components/Dashboard';
import Income from './components/Income';
import Expense from './components/Expense';
import Transactions from './components/Transactions';

function App() {
  const [mode, setMode] = useState('light') //hooks
  // const [isAuthenticated, isUserAuthenticated] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [credentials, setCredentials] = useState({});

  const login = (credentials) => {
    setIsLoggedIn(true);
    setCredentials(credentials);
  };

  const logout = () => {
    setIsLoggedIn(false);
    setCredentials({});
  };

  const toggleMode = () => {   //function of toggling
    if (mode === 'light') {
      setMode("dark")
      document.body.style.backgroundColor = "#343a40"
      document.title = "Blogging-DarkMode"
    }
    else {
      setMode("light")
      document.body.style.backgroundColor = "white"
      document.title = "Blogging-LightMode"
    }
  }
  return (
    <>
      <Router>
        <AuthContext.Provider
          value={{
            isLoggedIn,
            credentials,
            login,
            logout,
          }}
        >
          <Navbar title="Expense_Mgmt" toggleMode={toggleMode} mode={mode} credentials={credentials}></Navbar>
          <div className="container5">
            <Routes>
              <Route exact path="/" element={<Login mode={mode} />} />
              <Route exact path="/signup" element={<Signup mode={mode} />} />
              <Route exact path="/dashboard" element={<Dashboard />} />
              <Route exact path='/income' element={<Income />} />
              <Route exact path='/expense' element={<Expense />} />
              <Route exact path='/transactions' element={<Transactions />} />
            </Routes>
          </div>
          <div className="foot">
            <Footer mode={mode}></Footer>
          </div>
        </AuthContext.Provider>
      </Router>
    </>
  );
}

export default App;
