import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";


export default function Navbar(props) {
  const history = useNavigate();
  const handleLogout = (e) => {
    e.preventDefault();
    localStorage.removeItem('token');
    history("/");
  }

  const [credentials, setCredentials] = useState({});

  useEffect(() => {
    // Retrieve credentials from local storage
    const storedCredentials = localStorage.getItem('token');
    if (storedCredentials) {
      setCredentials(JSON.parse(storedCredentials.email));
    }
  }, []);

  return (
    <>
      <nav className={`navbar navbar-expand-lg navbar-${props.mode} bg-${props.mode}`}>
        <div className="container-fluid">
          <Link className="navbar-brand" to="https://www.linkedin.com/company/ww-in/">{props.title}</Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li class="nav-item">
                <Link class={`nav-link active`} aria-current="page" to="/blog">Home</Link>
              </li>
              <li class="nav-item">
                <Link class={`nav-link active`} aria-current="page" to="/write">Write</Link>
              </li>
              <li class="nav-item">
                <Link class={`nav-link active`} aria-current="page" to="/userdata">Your_Blogs</Link>
              </li>
              <li class="nav-item">
                <Link class={`nav-link active`} aria-current="page" to="/about">About</Link>
              </li>
            </ul>
            {!localStorage.getItem('token') ? <form className="d-flex">
              <Link className="btn btn-primary mx-1" to="/" role="button">Login</Link>
              <Link className="btn btn-primary mx-1" to="/signup" role="button">Signup</Link>
            </form> : <Link onClick={handleLogout} className="btn btn-primary">Logout <p>{credentials.email}</p></Link>}


            <div className={`form-check form-switch text-${props.mode === 'light' ? 'dark' : 'light'} mx-2 my-2`}>
              <input className="form-check-input" onClick={props.toggleMode} type="checkbox" role="switch" id="flexSwitchCheckDefault" />
              <label className="form-check-label" htmlFor="flexSwitchCheckDefault">Enable Dark</label>
            </div>

          </div>
        </div>
      </nav >
    </>
  )
}
