import React, { useEffect, useContext } from 'react'
import { useNavigate } from 'react-router-dom'
import Form from './Form';
import styled from 'styled-components'
import ExpContext from '../context/exp/ExpContext';
import IncomeItem from './Incomeitem';
function Income() {
    const context = useContext(ExpContext)
    const { totalIncome, total } = context;
    const history = useNavigate();
    useEffect(() => {
        if (localStorage.getItem('token')) {
            totalIncome();
            history("/income");

        }
        else {
            history("/");
        }
    }, [])
    return (
        <IncomeStyled>

            <div className="income-content">
                <div className="form-container">
                    <h2 className="total-income">Total Income: {total} </h2>
                    <h1>Incomes</h1>
                    <div class="container-fluid">
                        <div class="row">
                            <div class="col-md-5">
                                <Form />
                            </div>
                            <div class="col">

                                <h2>Right Side</h2>
                                <p>This is the right side content.</p>
                            </div>
                        </div>
                    </div>

                </div>

            </div>
        </IncomeStyled>
    )
}
const IncomeStyled = styled.div`
    display: flex;
    overflow: auto;
    .total-income{
        display: flex;
        justify-content: center;
        align-items: center;
        background: #FCF6F9;
        border: 2px solid #FFFFFF;
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        border-radius: 20px;
        padding: 1rem;
        margin: 1rem 0;
        font-size: 2rem;
        gap: .5rem;
        span{
            font-size: 2.5rem;
            font-weight: 800;
            color: var(--color-green);
        }
    }
    h1{
        color:red;
        text-align: center;
    }
    .income-content{
        display: flex;
        gap: 2rem;
        .incomes{
            flex: 1;
        }
    }
`;
export default Income
