import React from 'react'
import styled from 'styled-components'

function Button({ name, icon, onClick, bg, bPad, color, bRad }) {
    return (
        <div className={`bg-${props.mode} text-${props.mode === 'light' ? '' : 'light'}`}>
            <ButtonStyled style={{
                background: bg,
                padding: bPad,
                borderRadius: bRad,
                color: color,
            }} onClick={onClick}>
                {icon}
                {name}
            </ButtonStyled>
        </div>
    )
}

const ButtonStyled = styled.button`
    outline: none;
    border: none;
    font-family: inherit;
    font-size: inherit;
    display: flex;
    align-items: center;
    gap: .5rem;
    cursor: pointer;
    transition: all .4s ease-in-out;
    color:black;
`;


export default Button
