import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import "./Login.css";

export default function Login(){

    const history=useNavigate();

    const [email,setEmail]=useState(' ')
    const [password,setPassword]=useState(' ')

    async function submit(e){
        e.preventDefault();

        try{
            await axios.post("http://localhost:3000/login",{
                email,password
            }).then(res=>{
                if(res.data==="exist"){
                    history("/about")  //to go to after login
                }
                else if(res.data==="notexit"){
                    alert("User have not signup")
                }
            })
            .catch(e=>{
                alert("wrong details");
                console.log(e);
            })
        }
        catch(e){
            console.log(e);
        }
    }
    return(
    <>
    <h1>Login</h1>
    <div className="container my-2">
    <form action="POST">
  <div class="row mb-3">
    {/* <label for="inputEmail3" class="col-sm-2 col-form-label">Email</label> */}
    <div class="col-sm-3">
      <input type="email" onChange={(e)=>{setEmail(e.target.value)}} class="form-control"  placeholder="email"/>
    </div>
  </div>
  <div class="row mb-3">
    {/* <label for="inputPassword3" class="col-sm-2 col-form-label">Password</label> */}
    <div class="col-sm-3">
      <input type="password" class="form-control" onChange={(e)=>{setPassword(e.target.value)}} placeholder="password" />
    </div>
  </div>
  <input type="submit" class="btn btn-primary my-2" onClick={submit}/>
  <br />
  <Link to="/signup" class="btn btn-primary ">Click here to SignUp</Link>
</form>
    </div>
    </>
        // <div className={`login text-align-center justify-content-center`}>
        //     <h1>Login</h1>

        //     <form action="POST">
        //         <input type="email" onChange={(e)=>{setEmail(e.target.value)}} placeholder="Email"/>
        //         <br />
        //         <input type="password" onChange={(e)=>{setPassword(e.target.value)}} placeholder="Password" />
        //         <br />
        //         <input type="submit" onClick={submit}/>
        //     </form>
        //     <br />

        //     <p>Or</p>
        //     <br />

        //     <Link to="/signup">SignUp Page</Link>
        // </div>
    )
}
