import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

export default function Login(props) {

    const history = useNavigate()
    const [user, setUser] = useState(' ')
    const [email, setEmail] = useState(' ')
    const [password, setPassword] = useState(' ')

    async function submit(e) {
        e.preventDefault();

        try {
            await axios.post("http://localhost:3000/signup", {
                user, email, password
            })
                .then(res => {
                    if (res.data === "exist") {
                        history("/")
                        alert("User already exists")
                    }
                    else if (res.data === "notexist") {
                        history("/signup")
                    }
                })
                .catch(e => {
                    alert("wrong details");
                    console.log(e);
                })
        }
        catch (e) {
            console.log(e);
        }
    }
    return (
        <>
            <h1><img src="./download.png" alt="Loading" width={`100vh`} height={`100vh`} /></h1>
            <h1 className={`text-${props.mode === "light" ? "rgb(43, 45, 106)" : "white"}`}>SignUp</h1>
            <div className="container">
                <form action="POST" className="my-3" >
                    <div class="row mb-3">
                        {/* <label for="inputEmail3" class="col-sm-2 col-form-label">Email</label> */}
                        <div class="col-sm-5">
                            <input type="user" onChange={(e) => { setUser(e.target.value) }} class="form-control" placeholder="Username" />
                        </div>
                    </div>
                    <div class="row mb-3">
                        {/* <label for="inputEmail3" class="col-sm-2 col-form-label">Email</label> */}
                        <div class="col-sm-5">
                            <input type="email" onChange={(e) => { setDescription(e.target.value) }} class="form-control" placeholder="Description" />
                        </div>
                    </div>

                    <div>
                        <input type="submit" class="btn btn-primary mx-2 my-2" onClick={submit} />
                    </div>
                </form>
            </div>
        </>

    )
}
